import React, { useEffect, useState } from 'react'
import Cartes from './Cartes';
import axios from "axios";
import { useRecoilState } from 'recoil';
import { suggestions } from "../atoms/suggestions";

const Liste = () => {
    const [data, setData] = useRecoilState(suggestions);
    let [accepte, refuse] = [0,0];

    useEffect( ()=> {
        axios.get("http://malickkebe-api-simplon.herokuapp.com/api/idee")
        .then( response => setData(response.data));
    }, []);

    return (
        <div className="row">
            {data && data.map( idee => {idee.statut ? accepte++ : refuse++;return <Cartes key={idee.id} idee={idee}/>})}
        </div>
      )
}

export default Liste
